mapper.select:
SELECT menu_cd, parent_menu_cd, menu_nm, url, use_yn, require_auth_yn, ord_no, attr1 FROM menu WHERE 1=1
AND menu_cd = #{menu_cd}
AND parent_menu_cd = #{parent_menu_cd}

mapper.insert:
INSERT INTO menu (
	menu_cd
	, parent_menu_cd
	, menu_nm

	, attr1


	, require_auth_yn

) VALUES (
	#{menu_cd}
	, IFNULL(#{parent_menu_cd}, LPAD('0', 8, '0'))
	, #{menu_nm}

	, #{attr1}


	, #{require_auth_yn}

)

mapper.update:
UPDATE menu SET
	menu_nm = #{menu_nm}

	, parent_menu_cd = #{parent_menu_cd}


	, url = #{url}


	, use_yn = #{use_yn}


	, ord_no = #{ord_no}


	, require_auth_yn = #{require_auth_yn}


	, attr1 = #{attr1}

WHERE menu_cd =	#{menu_cd}
